mapper.select:
   SELECT gsoa.sid sid, gsoa.monitoring_stations_id monitoringStationsId, gsoa.stations_type stationsType, gsoa.investigate_date investigateDate,gsoa.contact_phone contactPhone, gsoa.has_litter hasLitter, 
   gsoa.has_sand hasSand, gsoa.has_surface_erosion hasSurfaceErosion, gsoa.has_saline_spot hasSalineSpot, gsoa.bare_land_area_ratio bareLandAreaRatio, gsoa.soil_moisture soilMoisture, gsoa.monitoring_period monitoringPeriod, 
    gsoa.coverage coverage, gsoa.fresh_amount freshAmount, gsoa.dry_amount dryAmount, gsoa.herb_grass_avg_height herbGrassAvgHeight, gsoa.herb_plant_nums herbPlantNums, gsoa.herb_bad_grass_nums herbBadGrassNums, 
    gsoa.herb_avg_fresh_amount herbAvgFreshAmount, gsoa.herb_avg_dry_amount herbAvgDryAmount, gsoa.herb_avg_edible_fresh_amount herbAvgEdibleFreshAmount, gsoa.herb_avg_edible_dry_amount herbAvgEdibleDryAmount, 
    gsoa.first_fresh_amount firstFreshAmount, gsoa.first_dry_amount firstDryAmount, gsoa.first_edible_fresh_amount firstEdibleFreshAmount, gsoa.first_edible_dry_amount firstEdibleDryAmount, 
    gsoa.second_fresh_amount secondFreshAmount, gsoa.second_dry_amount secondDryAmount, gsoa.second_edible_fresh_amount secondEdibleFreshAmount, gsoa.second_edible_dry_amount secondEdibleDryAmount, 
    gsoa.third_fresh_amount thirdFreshAmount, gsoa.third_dry_amount thirdDryAmount, gsoa.third_edible_fresh_amount thirdEdibleFreshAmount, gsoa.third_edible_dry_amount thirdEdibleDryAmount, 
    gsoa.shrubs_avg_height shrubsAvgHeight, gsoa.shrubs_nums shrubsNums, gsoa.shrubs_main_names shrubsMainNames, gsoa.shrubs_coverage_size shrubsCoverageSize, gsoa.shrubs_fresh_amount shrubsFreshAmount, 
    gsoa.shrubs_dry_amount shrubsDryAmount, gsoa.permanent_coverage permanentCoverage, gsoa.permanent_grass_avg_height permanentGrassAvgHeight, gsoa.permanent_shrubs_avg_height permanentShrubsAvgHeight, 
    gsoa.create_user_id createUserId, gsoa.create_user_name createUserName, gsoa.create_time createTime,gsoa.investigate_user_ids investigateUserIds, gsoa.investigate_user_names investigateUserNames, 
    gsoa.erosion_reason erosionReason, gsoa.herb_main_plant herbMainPlant, gsoa.remark remark,gms.city_code cityCode,gms.grass_category grassCategory,
    gms.grass_type grassType
 FROM `gms_shrubs_observation_area` gsoa LEFT JOIN gms_monitoring_stations gms ON gsoa.monitoring_stations_id=gms.sid


    	city_code like '%${cityCode}%'


    	and grass_category = #{grassCategory}


    	and grass_type = #{grassType}


    	and soil_moisture >= #{beginSoilMoisture}


    	and soil_moisture <= #{endSoilMoisture}


    	and coverage >= #{beginCoverage}


    	and coverage <=#{endCoverage}


    	and fresh_amount >= #{beginFreshAmount}


    	and fresh_amount <= #{endFreshAmount}


    	and end_dry_amount >= #{beginDryAmount}


    	and end_dry_amount <= #{endDryAmount}


    	and DATE_FORMAT(investigate_date,'%Y') >= #{startYear,jdbcType=INTEGER}


    	and DATE_FORMAT(investigate_date,'%Y') <=#{endYear,jdbcType=INTEGER}


    	and MONTH(investigate_date) = #{month}


    	and stations_type = #{stationsType}


    	and gsoa.sid in

				#{item}




			order by ${sort}

				${order}
